// Parse Perplexity response and extract JSON
const response = $input.item.json;
let policyData;

try {
  // Try to extract JSON from response
  const jsonMatch = response.content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    policyData = JSON.parse(jsonMatch[0]);
  } else {
    policyData = JSON.parse(response.content);
  }
} catch (e) {
  // Fallback: try to extract structured data
  policyData = {
    return_window_days: 30, // default
    has_free_returns: false,
    policy_description: response.content,
    return_portal_url: null,
    website_url: $input.item.json.body.website_url || null
  };
}

// Validate and set defaults
if (!policyData.return_window_days || policyData.return_window_days < 0) {
  policyData.return_window_days = 30;
}

if (typeof policyData.has_free_returns !== 'boolean') {
  policyData.has_free_returns = false;
}

// Generate retailer ID from name
const retailerName = $input.item.json.body.retailer_name;
const retailerId = retailerName.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-*|-*$/g, '');

return {
  json: {
    retailer_id: retailerId,
    retailer_name: retailerName,
    return_window_days: policyData.return_window_days,
    has_free_returns: policyData.has_free_returns,
    policy_description: policyData.policy_description || null,
    website_url: policyData.website_url || $input.item.json.body.website_url || null,
    return_portal_url: policyData.return_portal_url || null,
    user_id: $input.item.json.body.user_id
  }
};

// Format response for existing retailer
const retailer = $input.item.json[0];
return {
  json: {
    retailer_id: retailer.id,
    retailer_name: retailer.name,
    return_window_days: retailer.return_window_days,
    has_free_returns: retailer.has_free_returns,
    website_url: retailer.website_url,
    return_portal_url: retailer.return_portal_url || null,
    status: 'existing'
  }
};

// Format response for created retailer
const retailer = $input.item.json;
return {
  json: {
    retailer_id: retailer.id,
    retailer_name: retailer.name,
    return_window_days: retailer.return_window_days,
    has_free_returns: retailer.has_free_returns,
    website_url: retailer.website_url,
    return_portal_url: retailer.return_portal_url || null,
    status: 'created'
  }
};
