// After creating retailer, format it to match the expected structure
const createdRetailer = $input.item.json;
const parseData = $('Parse Invoice Data1').item.json;

console.log('Created retailer:', createdRetailer);
console.log('Parse data:', parseData);

// Ensure items array is preserved for Split Items node
return {
  json: {
    retailer_id: createdRetailer.id,
    retailer_name: createdRetailer.name,
    retailer_found: true,
    seller_name: parseData.seller_name,
    total_amount: parseData.total_amount,
    items: parseData.items || [], // Ensure items array is preserved
    user_id: parseData.user_id, // Preserve user_id
    raw_response: parseData.raw_response
  }
};

// Process all items in runOnceForAllItems mode
const results = [];

// Get user_id from webhook (preserve it through the flow)
const webhookData = $('Webhook Trigger').item.json;
const userId = webhookData.user_id || webhookData.userId;

for (const input of $input.all()) {
  let invoiceData;

  try {
    let content;

    // Handle the specific Mistral/Claude response format
    if (input.json.output && Array.isArray(input.json.output) && input.json.output[0]) {
      // Your specific format: output[0].content[0].text
      const message = input.json.output[0];
      if (message.content && Array.isArray(message.content) && message.content[0]) {
        content = message.content[0].text;
      }
    }
    // Fallback for other AI response formats
    else if (input.json.choices && input.json.choices[0]?.message?.content) {
      content = input.json.choices[0].message.content;
    } 
    else if (input.json.content) {
      content = input.json.content;
    } 
    else if (typeof input.json === 'string') {
      content = input.json;
    } 
    else {
      content = JSON.stringify(input.json);
    }

    // If content is already an object, use it directly
    if (typeof content === 'object') {
      invoiceData = content;
    } else {
      // Extract JSON from string content (handle markdown code blocks)
      const jsonMatch = content.match(/```(?:json)?\s*([\s\S]*?)```/) || content.match(/\{[\s\S]*\}/);

      if (jsonMatch) {
        const jsonString = jsonMatch[1] || jsonMatch[0];
        invoiceData = JSON.parse(jsonString.trim());
      } else {
        invoiceData = JSON.parse(content);
      }
    }

  } catch (e) {
    throw new Error(`Failed to parse invoice data: ${e.message}. Response: ${JSON.stringify(input.json)}`);
  }

  // Validate required fields (matching your schema)
  if (!invoiceData.seller_name) {
    throw new Error('Seller name not found in invoice');
  }

  if (!invoiceData.items || !Array.isArray(invoiceData.items) || invoiceData.items.length === 0) {
    throw new Error('No items found in invoice');
  }

  // Map items to ensure all required fields exist
  const mappedItems = invoiceData.items.map(item => ({
    item_name: item.item_name || 'Unnamed Item',
    item_cost: item.item_cost || 0,
    item_quantity: item.item_quantity || 1,
    item_currency: item.item_currency || 'USD'
  }));

  // Calculate total if not provided
  const totalAmount = mappedItems.reduce((sum, item) => 
    sum + (item.item_cost * item.item_quantity), 0
  );

  results.push({
    json: {
      seller_name: invoiceData.seller_name,
      total_amount: totalAmount,
      items: mappedItems,
      user_id: userId, // Preserve user_id from webhook
      raw_response: input.json // Keep original for debugging
    }
  });
}

return results;

// Match retailer from search results
// Handle empty response case - n8n might not output empty arrays
let searchResults = $input.item.json;

// If no input data, check if it's because of empty array
if (!searchResults || (typeof searchResults === 'object' && Object.keys(searchResults).length === 0)) {
  // Try to get data from previous node's output
  const previousNode = $('Search Retailer2');
  if (previousNode && previousNode.item && previousNode.item.json) {
    searchResults = previousNode.item.json;
  }
}

// Fix: Use seller_name instead of retailer_name
const invoiceRetailerName = $('Parse Invoice Data1').item.json.seller_name;
const invoiceRetailerNameLower = invoiceRetailerName.toLowerCase();

// Debug: Log the search results
console.log('Search results type:', typeof searchResults);
console.log('Search results:', JSON.stringify(searchResults));
console.log('Looking for retailer:', invoiceRetailerNameLower);

let matchedRetailer = null;

// Handle case where API returns an array directly or wrapped
let results = null;
if (Array.isArray(searchResults)) {
  results = searchResults;
} else if (searchResults && Array.isArray(searchResults.data)) {
  results = searchResults.data;
} else if (searchResults && searchResults.json && Array.isArray(searchResults.json)) {
  results = searchResults.json;
} else if (searchResults && typeof searchResults === 'object') {
  // Try to find array in the object
  const keys = Object.keys(searchResults);
  for (const key of keys) {
    if (Array.isArray(searchResults[key])) {
      results = searchResults[key];
      break;
    }
  }
}

// If no results found, signal that retailer needs to be created
if (!results || results.length === 0) {
  console.log(`Retailer '${invoiceRetailerName}' not found. Will create with default values.`);
  return {
    json: {
      retailer_found: false,
      retailer_name: invoiceRetailerName,
      needs_creation: true,
      default_return_window_days: 30, // Default 30-day return window
      ...$('Parse Invoice Data1').item.json
    }
  };
}

// Try to match retailer
if (results && results.length > 0) {
  // Try exact match first
  matchedRetailer = results.find(r => r.name && r.name.toLowerCase() === invoiceRetailerNameLower);

  // Try fuzzy match
  if (!matchedRetailer) {
    matchedRetailer = results.find(r => r.name && (r.name.toLowerCase().includes(invoiceRetailerNameLower) || invoiceRetailerNameLower.includes(r.name.toLowerCase())));
  }

  // Take first result if no match
  if (!matchedRetailer && results.length > 0) {
    matchedRetailer = results[0];
  }
}

// If still no match, signal creation needed
if (!matchedRetailer) {
  console.log(`Retailer '${invoiceRetailerName}' not matched from ${results.length} results. Will create with default values.`);
  return {
    json: {
      retailer_found: false,
      retailer_name: invoiceRetailerName,
      needs_creation: true,
      default_return_window_days: 30,
      ...$('Parse Invoice Data1').item.json
    }
  };
}

// Retailer found - return it
console.log(`Retailer matched: ${matchedRetailer.name} (ID: ${matchedRetailer.id})`);
return {
  json: {
    retailer_found: true,
    retailer_id: matchedRetailer.id,
    retailer_name: matchedRetailer.name,
    needs_creation: false,
    ...$('Parse Invoice Data1').item.json
  }
};

// Aggregate results
const items = $input.all();
const itemsCreated = items.map(item => item.json.id || item.json.item_id);

// Get retailer name from Match Retailer node
const retailerName = $('Match Retailer2').item.json.retailer_name;

return {
  json: {
    items_created: itemsCreated,
    retailer_matched: retailerName,
    items_count: itemsCreated.length,
    errors: []
  }
};

