// Validate and log data before creating return item
// This runs AFTER Split Items2, so we should have individual items
const data = $input.item.json;

console.log('=== Validate Data Before Create ===');
console.log('Available fields:', Object.keys(data).join(', '));
console.log('Data:', JSON.stringify(data, null, 2));

// Validate retailer_id (required by API)
if (!data.retailer_id) {
  throw new Error(`Missing retailer_id! Available fields: ${Object.keys(data).join(', ')}`);
}

// If we still have items array, Split Items2 didn't split - this shouldn't happen
// but let's handle it gracefully
if (data.items && Array.isArray(data.items) && !data.item_name) {
  console.error('ERROR: Split Items2 did not split items array!');
  console.error('Items array:', JSON.stringify(data.items, null, 2));
  throw new Error(`Split Items2 failed! Still receiving parent object. Items array has ${data.items.length} items. Available fields: ${Object.keys(data).join(', ')}`);
}

// Validate item fields (should be present after Split Items2)
if (!data.item_name) {
  console.error('Missing item_name. Full data:', JSON.stringify(data, null, 2));
  throw new Error(`Missing item_name! Available fields: ${Object.keys(data).join(', ')}`);
}

// Set default for item_cost if missing
if (data.item_cost === undefined || data.item_cost === null) {
  console.warn('item_cost is missing, defaulting to 0');
  data.item_cost = 0;
}

console.log('Validation passed. retailer_id:', data.retailer_id, 'item_name:', data.item_name);

// Pass through all data
return { json: data };
