// After creating retailer, format it to match the expected structure
const createdRetailer = $input.item.json;
const parseData = $('Parse Invoice Data').item.json;

console.log('Created retailer:', createdRetailer);
console.log('Parse data:', parseData);

// Ensure items array is preserved for Split Items node
return {
  json: {
    retailer_id: createdRetailer.id,
    retailer_name: createdRetailer.name,
    retailer_found: true,
    seller_name: parseData.seller_name,
    total_amount: parseData.total_amount,
    items: parseData.items || [], // Ensure items array is preserved
    user_id: parseData.user_id, // Preserve user_id
    raw_response: parseData.raw_response
  }
};

// Process all items in runOnceForAllItems mode
const results = [];

// Get user_id from webhook (preserve it through the flow)
const webhookData = $('Webhook Trigger').item.json;
const userId = webhookData.user_id || webhookData.userId;

for (const input of $input.all()) {
  let invoiceData;

  try {
    let content;

    // Handle the specific Mistral/Claude response format
    if (input.json.output && Array.isArray(input.json.output) && input.json.output[0]) {
      // Your specific format: output[0].content[0].text
      const message = input.json.output[0];
      if (message.content && Array.isArray(message.content) && message.content[0]) {
        content = message.content[0].text;
      }
    }
    // Fallback for other AI response formats
    else if (input.json.choices && input.json.choices[0]?.message?.content) {
      content = input.json.choices[0].message.content;
    } 
    else if (input.json.content) {
      content = input.json.content;
    } 
    else if (typeof input.json === 'string') {
      content = input.json;
    } 
    else {
      content = JSON.stringify(input.json);
    }

    // If content is already an object, use it directly
    if (typeof content === 'object') {
      invoiceData = content;
    } else {
      // Extract JSON from string content (handle markdown code blocks)
      const jsonMatch = content.match(/```(?:json)?\s*([\s\S]*?)```/) || content.match(/\{[\s\S]*\}/);

      if (jsonMatch) {
        const jsonString = jsonMatch[1] || jsonMatch[0];
        invoiceData = JSON.parse(jsonString.trim());
      } else {
        invoiceData = JSON.parse(content);
      }
    }

  } catch (e) {
    throw new Error(`Failed to parse invoice data: ${e.message}. Response: ${JSON.stringify(input.json)}`);
  }

  // Validate required fields (matching your schema)
  if (!invoiceData.seller_name) {
    throw new Error('Seller name not found in invoice');
  }

  if (!invoiceData.items || !Array.isArray(invoiceData.items) || invoiceData.items.length === 0) {
    throw new Error('No items found in invoice');
  }

  // Map items to ensure all required fields exist
  const mappedItems = invoiceData.items.map(item => ({
    item_name: item.item_name || 'Unnamed Item',
    item_cost: item.item_cost || 0,
    item_quantity: item.item_quantity || 1,
    item_currency: item.item_currency || 'USD',
    currency_symbol: item.currency_symbol || ''
  }));

  // Calculate total if not provided
  const totalAmount = mappedItems.reduce((sum, item) => 
    sum + (item.item_cost * item.item_quantity), 0
  );

  results.push({
    json: {
      seller_name: invoiceData.seller_name,
      total_amount: totalAmount,
      items: mappedItems,
      user_id: userId, // Preserve user_id from webhook
      raw_response: input.json // Keep original for debugging
    }
  });
}

return results;

// Aggregate results
const items = $input.all();

// Map detailed item information including currency fields
const itemsCreated = items.map(item => ({
  id: item.json.id || item.json.item_id,
  name: item.json.name,
  price: item.json.price,
  currency: item.json.currency,
  currency_symbol: item.json.currency_symbol,
  quantity: item.json.quantity
}));

// Get retailer name from Match Retailer node
const retailerName = $('Match Retailer').item.json.retailer_name;

return {
  json: {
    items_created: itemsCreated,
    retailer_matched: retailerName,
    items_count: itemsCreated.length,
    errors: []
  }
};

