// IMPORTANT: HTTP node splits arrays into individual items
// So $input.item.json is the retailer object itself, not an array!
const searchResult = $input.item.json;

console.log('=== Match Retailer Debug ===');
console.log('Search result:', JSON.stringify(searchResult, null, 2));

// Get seller name from invoice
const invoiceRetailerName = $('Parse Invoice Data').item.json.seller_name;
const invoiceRetailerNameLower = invoiceRetailerName.toLowerCase().trim();

console.log('Looking for retailer:', invoiceRetailerNameLower);

// Check if we have a valid retailer object
if (!searchResult || !searchResult.id) {
  console.log('❌ No valid retailer data received');
  return {
    json: {
      retailer_found: false,
      retailer_name: invoiceRetailerName,
      needs_creation: true,
      default_return_window_days: 30,
      ...$('Parse Invoice Data').item.json
    }
  };
}

// We have a retailer! Now check if it matches
const retailerName = (searchResult.name || '').toLowerCase().trim();
const retailerId = (searchResult.id || '').toLowerCase().trim();

console.log('Found retailer:', retailerName, '(ID:', retailerId, ')');

// Check if names match (they should since we searched by name)
const isMatch = retailerName === invoiceRetailerNameLower ||
                retailerId.includes(invoiceRetailerNameLower) ||
                invoiceRetailerNameLower.includes(retailerName);

if (isMatch) {
  console.log('✅ MATCH CONFIRMED:', searchResult.name);
  return {
    json: {
      retailer_found: true,
      retailer_id: searchResult.id,
      retailer_name: searchResult.name,
      return_window_days: searchResult.return_window_days || 30,
      website_url: searchResult.website_url,
      has_free_returns: searchResult.has_free_returns || false,
      policy_description: searchResult.policy_description,
      ...$('Parse Invoice Data').item.json
    }
  };
} else {
  console.log('❌ No match:', retailerName, 'vs', invoiceRetailerNameLower);
  return {
    json: {
      retailer_found: false,
      retailer_name: invoiceRetailerName,
      needs_creation: true,
      default_return_window_days: 30,
      ...$('Parse Invoice Data').item.json
    }
  };
}

